import{getShortContent as t}from"./utils.js";import{CONFIG as e}from"./config.js";const o=e.API_URL;export async function getProjects(){try{const e=await fetch(`${o}/projects/`);if(!e.ok)throw new Error(`HTTP error! status: ${e.status}`);const n=await e.json(),c=document.getElementById("project-container");n.data.forEach((e=>{const n=t(e.content.trim(),63),r=t(e.title,35),a=document.createElement("div");a.className="card",a.innerHTML=`\n        <a href="/project-detail.html?id=${e.id}" class="project-detail-link">\n          <img class="project-img" src="${o}/${e.img}" alt="Project Image" loading="lazy">\n          <div class="project-content">\n            <h1> ${r} </h1>\n              <p> ${n} </p>\n          </div>\n        </a>\n        `,c.appendChild(a)}))}catch(t){console.error("Failed to load projects:",t)}}